import React, { useEffect, useState } from 'react'
import PostListItem from '../../../../components/PostListItem'
import { SERVER_URL } from '../../../../config/settings'

export default function Explore() {
    const [myProfile, setMyProfile] = useState(null)

    useEffect(() => {
        fetch(`${SERVER_URL}/profile-api/myprofile`)
            .then(response => response.json())
            .then(data => setMyProfile(data))
    }, [])

    useEffect(() => {
        document.querySelector('.hashtag-container') &&
            document.querySelector('.hashtag-container').classList.add('active')
    }, [myProfile])

    const switchPage = e => {
        const element = e.target
        document.querySelectorAll('.hashtag-container').forEach(el => {
            el.classList.remove('active')
        })
        element.classList.add('active')
    }

    return (
        <div className="explore-page-container">{console.log(myProfile)}
            {myProfile !== null ?
                <>
                    <div className="hashtags-header">
                        {myProfile.interests.map(interest => {
                            return (
                                <div className="hashtag-container" onClick={switchPage}>
                                    {interest.title}
                                </div>
                            )
                        })}
                    </div>
                    <div className="post-list">
                        
                    </div>
                </>
                :
                <div className="loader-container">
                    <div className="loader" />
                </div>
            }
        </div>
    )
}
