import React, { useEffect, useState } from 'react'

import FeedSidebar from './components/feed-sidebar/FeedSidebar'
import ProfileSidebar from './components/profile-sidebar/ProfileSidebar'


export default function SidebarRight(props) {
    const [page, setPage] = useState(props.page)
    const [counter, forceRerender] = useState(0)

    useEffect(() => {
        setPage(props.match.params.path ? props.match.params.path : props.page)
        if (page === props.match.params.path || page === props.page) {
            forceRerender(counter + 1)
        }
    }, [props])

    const pages = {
        'home': <FeedSidebar />,
        'perfil': <ProfileSidebar />,
        'user': <ProfileSidebar />,
    }

    return (
        <div className="sidebar p-0 b-left" style={{ right: '0' }}>{console.log('rendering...')}
            {pages[page]}
        </div>
    )
}
