import React from 'react'


export default function News() {
    var state = {
        articles: []
    }
    fetch('https://newsapi.org/v2/everything?q=bitcoin%20OR%20ethereum&language=pt&apiKey=ff76727063ed40308672d1f49127bd43')
        .then(res => res.json())
        .then((data) => {
          this.setState({ articles: data })
        }).catch(console.log)

    return (
        <>
            <div>
                <div>
                    <center><h1>Article List</h1></center>
                    {articles.map((article) => (
                        <div>
                            <div>
                                <h5>{article.author}</h5>
                                <h6>{article.title}</h6>
                                <p>{article.description}</p>
                            </div>
                        </div>
                    ))}
                </div>
                <div className="profiles-loader-container">
                        <div className="loader" />
                </div>
             </div>
        </>
    )
}
