import React, { useState, useContext } from 'react'

import { ProfileImageContext, ImageCropModalContext, EditProfileModalContext } from '../../../context/edit-profile/EditProfileContext'
import CroppieModal from './CroppieModal'
import EditProfileModal from './EditProfileModal'


export default function EditProfile(props) {
    const [profileImage, setProfileImage] = useContext(ProfileImageContext)
    const [imageCropModalIsOpen, setImageCropModalIsOpen] = useContext(ImageCropModalContext)
    const [editProfileModalIsOpen, setEditProfileModalIsOpen] = useContext(EditProfileModalContext)

    const handleProfileImageChange = (e, croppieModalIsOpen) => {
        if (croppieModalIsOpen) props.closeEditingProfileModal()

        const reader = new FileReader()
        reader.onload = () => {
            if (reader.readyState === 2) {
                setCroppieModalIsOpen(croppieModalIsOpen)
                setProfileImage(reader.result)
            }
        }
        try {
            reader.readAsDataURL(e.target.files[0])
        } catch {
    
        }
    }

    return (
        <>
            <CroppieModal
                image={profileImage}
                isOpen={imageCropModalIsOpen}
                closeModal={() => setImageCropModalIsOpen(false)}
                handleProfileImageChange={e => handleProfileImageChange(e, false)}
            />
            <EditProfileModal
                profile={props.profile}
                isOpen={editProfileModalIsOpen}
                closeModal={() => setEditProfileModalIsOpen(false)}
                handleProfileImageChange={e => handleProfileImageChange(e, true)}
            />
        </>
    )
}
