import React, { useEffect, useState } from 'react'

import FeedSidebar from './components/feed-sidebar/FeedSidebar'
import ProfileSidebar from './components/profile-sidebar/ProfileSidebar'


export default function SidebarRight() {
    let initialPage

    useEffect(() => {
        const url = window.location.href.split('/')
        console.log(url, window.location.href)
        if (url.includes('home')) initialPage = 'home'
        if (url.includes('perfil') || url.includes('user')) initialPage = 'profile'
    }, [])

    const [page, setPage] = useState(initialPage)

    return (
        <div className="sidebar p-0 b-left" style={{ right: '0' }}>
            {page === 'home' &&
                <FeedSidebar />
            }
            {page === 'profile' &&
                <ProfileSidebar />
            }
        </div>
    )
}
