import React from 'react'


export default function News() {
    var news = fetch('https://newsapi.org/v2/everything?q=bitcoin%20OR%20ethereum&language=pt&apiKey=ff76727063ed40308672d1f49127bd43').then((response)=>{
            return response.json();
          })[3];
    

    return (
        <>
            {<div className="news-container">
                        {news.map((data, key) => {
                        return (
                            <div key={key}>
                            {data.source +
                                " , " +
                                data.author +
                                " ," +
                                data.title +
                                ", " +
                                data.description}
                            </div>
                        );
                        })}
                    </div>
            }
            <div className="profiles-loader-container">
                    <div className="loader" />
            </div>
        </>
    )
}
